package Basics;
//Field-> A variable with values is known as field
//Attributes->The name of the variable.

public class OperatorandOperand {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b;//attribute
	    a=10;b=20; //field
	    int result=a+b; 
	    //+->A symbol or keyword used to perform operations on operands
	    //Operand a,b->The values or variables on which the operator acts on it. 
	    System.out.println(result);
		

	}

}
